var path = require("path"),
    fs = require('fs');
	
	
exports.isFile = function(path) {
	if (!fs.existsSync(path)) {
		return false;
	}
	var stat = fs.statSync(path);
	return stat.isFile();
};

exports.isDir = function(path) {
	if (!fs.existsSync(path)) {
		return false;
	}
	var stat = fs.statSync(path);
	return stat.isDirectory();
};